mapper.select:
SELECT *
FROM products_review
WHERE product_id = #{productId}
LIMIT ${(pageNum - 1) * n}, #{n};

SELECT *
FROM users_review
WHERE user_id = #{userId}
    LIMIT ${(pageNum - 1) * n}, #{n};

SELECT AVG(rating)
FROM products_review
WHERE product_id = #{productId};

SELECT AVG(rating)
FROM users_review
WHERE user_id = #{userId};
